import React, { useState, useEffect } from 'react';
import axios from 'axios';

const YoutubeComponent = () => {
  const [videos, setVideos] = useState([]);

  useEffect(() => {
    const fetchYoutubeData = async () => {
      try {
        // Remplacez 'YOUR_API_KEY' par votre clé API YouTube
        const API_KEY = 'AIzaSyAa4AjRaQBvfrP6C5_3P_z75a5y1AYLrQw';
        const CHANNEL_ID = 'UC5H7jK_2reQHA1XxoNi7DDw'; // Exemple de chaîne Renault

        const response = await axios.get(
          `https://www.googleapis.com/youtube/v3/search?key=${API_KEY}&channelId=${CHANNEL_ID}&part=snippet,id&order=date&maxResults=5`
        );

        setVideos(response.data.items);
      } catch (error) {
        console.error('Error fetching data from YouTube API', error);
      }
    };

    fetchYoutubeData();
  }, []);

  return (
    <div>
      <h2>Vidéos YouTube de la chaîne Renault</h2>
      <ul>
        {videos.map(video => (
          <li key={video.id.videoId}>
            <iframe
              width="560"
              height="315"
              src={`https://www.youtube.com/embed/${video.id.videoId}`}
              title={video.snippet.title}
              frameBorder="0"
              allowFullScreen
            ></iframe>
            <p>{video.snippet.title}</p>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default YoutubeComponent;
